			tag.colorPattern || (tag.colorPattern = colorArr[colorCount++]);
			/*tagConfig本来没有rows或cols，是大块。执行else部分*/
			if (tag.rows || tag.cols) {
				_cutGrid(tag, arguments.callee);
			}
			else {
				var s = tag.colorPattern,
